// Ensure jQuery is loaded
$(document).ready(function() {
    // Mobile Menu
    $('#menu-bars').on('click', function() {
        $(this).toggleClass('fa-times');
        $('.navbar').toggleClass('active');
    });

    $(window).on('scroll', function() {
        $('#menu-bars').removeClass('fa-times');
        $('.navbar').removeClass('active');
    });

    // Mode Toggle
    $('.mode-toggle').on('click', function() {
        $('body').toggleClass('dark-mode');
        $('#mode-icon').text($('body').hasClass('dark-mode') ? 'üåû' : 'üåú');
    });

    // Event Storage (Simulating a database)
    let events = [
        { name: "Music Concert", category: "Concert", description: "Experience an unforgettable night of music with top artists!", date: "2025-03-15T18:00:00", location: "City Arena", timerId: "timer-1", isUserCreated: false },
        { name: "Tech Summit", category: "Conference", description: "Join industry leaders to discuss the future of technology.", date: "2025-04-01T09:00:00", location: "Tech Center", timerId: "timer-2", isUserCreated: false },
        { name: "Art Gallery", category: "Exhibition", description: "Discover stunning artworks from local and international artists.", date: "2025-03-20T10:00:00", location: "Art Museum", timerId: "timer-3", isUserCreated: false },
        { name: "Coding Workshop", category: "Workshop", description: "Learn advanced coding techniques from expert developers.", date: "2025-03-25T14:00:00", location: "Community Center", timerId: "timer-4", isUserCreated: false },
        { name: "Food Festival", category: "Festival", description: "Taste cuisines from around the world in this culinary celebration!", date: "2025-04-10T12:00:00", location: "Central Park", timerId: "timer-5", isUserCreated: false },
        { name: "Jazz Night", category: "Concert", description: "Enjoy a soulful evening of jazz music under the stars.", date: "2025-04-15T20:00:00", location: "Rooftop Lounge", timerId: "timer-6", isUserCreated: false },
        { name: "AI Conference", category: "Conference", description: "Explore the latest advancements in artificial intelligence.", date: "2025-05-01T10:00:00", location: "Convention Hall", timerId: "timer-7", isUserCreated: false },
        { name: "Photography Exhibition", category: "Exhibition", description: "Showcase of breathtaking photography from renowned artists.", date: "2025-04-20T11:00:00", location: "Gallery Space", timerId: "timer-8", isUserCreated: false }
    ];

    let timerCounter = 8;
    let ratings = {
        "Music Concert": { stars: [4, 5, 4], comments: ["Great night out!", "Loved the energy.", "Sound could be better."] },
        "Tech Summit": { stars: [4, 4, 5], comments: ["Very informative.", "Good speakers.", "Networking was top-notch."] },
        "Art Gallery": { stars: [5, 4, 5], comments: ["Stunning pieces!", "Well-curated.", "A must-see exhibit."] },
        "Coding Workshop": { stars: [3, 4, 4], comments: ["Good intro.", "Hands-on was helpful.", "Pace was a bit fast."] },
        "Food Festival": { stars: [5, 5, 4], comments: ["Amazing food!", "So many options.", "Crowded but fun."] },
        "Jazz Night": { stars: [4, 5, 4], comments: ["Soulful vibes.", "Perfect evening.", "Seating was tight."] },
        "AI Conference": { stars: [4, 4, 5], comments: ["Mind-blowing tech!", "Great insights.", "Venue was packed."] },
        "Photography Exhibition": { stars: [5, 5, 4], comments: ["Beautiful shots!", "Inspiring work.", "Lighting was perfect."] }
    };

    const categoryImages = {
        Concert: 'data:image/jpeg;base64,',
        Conference: 'data:image/jpeg;base64,',
        Exhibition: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBlOhb0NieVy8EcTdlypd8cWWBzRITWh9b9A&s',
        Workshop: 'data:image/jpeg;base64,',
        Festival: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSV-Q-TNSYfRYLS9gzI4kBIrKc62PzXSlruQg&s'
    };

    // Temporary JSON Storage for New Events
    let tempEvents = JSON.parse(localStorage.getItem('tempEvents')) || [];

    // Expanded Challenges System
    let challenges = [
        { id: "attend-3", name: "Event Enthusiast", description: "Attend 3 events", goal: 3, badge: "üèÖ" },
        { id: "create-2", name: "Event Planner", description: "Create 2 events", goal: 2, badge: "üéâ" },
        { id: "rate-5", name: "Critic", description: "Rate 5 events", goal: 5, badge: "‚≠ê" },
        { id: "invite-10", name: "Social Butterfly", description: "Invite 10 friends", goal: 10, badge: "ü¶ã" },
        { id: "attend-concert-2", name: "Concert Lover", description: "Attend 2 concerts", goal: 2, badge: "üéµ" },
        { id: "create-festival-1", name: "Festival Master", description: "Create 1 festival", goal: 1, badge: "üé°" },
        { id: "rate-conference-3", name: "Conference Guru", description: "Rate 3 conferences", goal: 3, badge: "üìä" },
        { id: "attend-workshop-4", name: "Workshop Wizard", description: "Attend 4 workshops", goal: 4, badge: "üõ†Ô∏è" }
    ];

    // Initialize userProgress with defaults for all properties
    let userProgress = JSON.parse(localStorage.getItem('userProgress')) || {};
    userProgress = {
        attended: userProgress.attended || 0,
        created: userProgress.created || 0,
        rated: userProgress.rated || 0,
        invited: userProgress.invited || 0,
        attendedConcerts: userProgress.attendedConcerts || 0,
        createdFestivals: userProgress.createdFestivals || 0,
        ratedConferences: userProgress.ratedConferences || 0,
        attendedWorkshops: userProgress.attendedWorkshops || 0
    };

    function saveProgress() {
        localStorage.setItem('userProgress', JSON.stringify(userProgress));
    }

    function saveTempEvents() {
        localStorage.setItem('tempEvents', JSON.stringify(tempEvents));
    }

    // Full-Screen Celebration Function
    function triggerCelebration() {
        const overlay = $('#celebration-overlay');
        overlay.empty().show();

        for (let i = 0; i < 50; i++) {
            const confetti = $('<div class="confetti"></div>');
            const colors = ['#2196F3', '#f1c40f', '#e74c3c', '#2ecc71', '#9b59b6'];
            confetti.css({
                left: `${Math.random() * 100}vw`,
                background: colors[Math.floor(Math.random() * colors.length)],
                animationDelay: `${Math.random() * 2}s`
            });
            overlay.append(confetti);
        }

        setTimeout(() => overlay.fadeOut(500), 3000);
    }

    // Modal Functionality
    window.showEventDetails = function(eventName) {
        const modal = $('#event-modal');
        const inviteForm = $('#invite-form');
        const event = events.find(e => e.name === eventName);

        $('#modal-title').text(eventName);
        $('#modal-details').html(`
            <p><span>Date:</span> ${formatDate(event.date)}</p>
            <p><span>Location:</span> ${event.location}</p>
            <p><span>Category:</span> ${event.category}</p>
        `);
        $('#modal-description').text(event.description);
        modal.css('display', 'block');
        inviteForm.hide();
        $('#invite-confirmation').text('');

        $('#modal-register').off('click').on('click', function() {
            modal.css('display', 'none');
            $('html, body').animate({ scrollTop: $('#register').offset().top }, 'smooth');
            $('#event').val(eventName);
        });

        $('#modal-invite').off('click').on('click', function() {
            inviteForm.toggle();
        });

        $('#invite-form').off('submit').on('submit', function(e) {
            e.preventDefault();
            const email = $('#invite-email').val();
            const message = $('#invite-message').val() || `Check out ${eventName} on ${formatDate(event.date)} at ${event.location}!`;
            const previousInvited = userProgress.invited;
            userProgress.invited++;
            saveProgress();
            if (previousInvited < 10 && userProgress.invited >= 10) {
                triggerCelebration();
                renderChallenges();
            } else {
                renderChallenges();
            }
            $('#invite-confirmation').text(`Invitation sent to ${email} for ${eventName}!`);
            $(this)[0].reset();
            setTimeout(() => {
                inviteForm.hide();
                $('#invite-confirmation').text('');
            }, 2000);
        });

        ratings[eventName] = ratings[eventName] || { stars: [], comments: [] };
        const eventRating = ratings[eventName];
        const average = eventRating.stars.length > 0 ? (eventRating.stars.reduce((a, b) => a + b, 0) / eventRating.stars.length).toFixed(1) : 0;
        $('#average-rating').text(average);
        $('#rating-count').text(eventRating.stars.length);
        $('.star').removeClass('filled');
        $('#reviews-list').html(eventRating.comments.slice().reverse().map(c => `<p>${c}</p>`).join(''));

        const starClickHandler = function() {
            const value = parseInt($(this).data('value'));
            ratings[eventName].stars.push(value);
            const newAverage = (ratings[eventName].stars.reduce((a, b) => a + b, 0) / ratings[eventName].stars.length).toFixed(1);
            $('#average-rating').text(newAverage);
            $('#rating-count').text(ratings[eventName].stars.length);
            $('.star').removeClass('filled');
            for (let i = 0; i < value; i++) {
                $('.star').eq(i).addClass('filled');
            }
        };

        $('.star').css('cursor', 'pointer').off('click').on('click', starClickHandler);

        $('#submit-comment').off('click').on('click', function() {
            const comment = $('#review-comment').val();
            if (comment) {
                const previousRated = userProgress.rated;
                const previousRatedConferences = userProgress.ratedConferences;
                ratings[eventName].comments.push(comment);
                userProgress.rated++;
                if (event.category === "Conference") userProgress.ratedConferences++;
                saveProgress();
                if (previousRated < 5 && userProgress.rated >= 5) {
                    triggerCelebration();
                }
                if (previousRatedConferences < 3 && userProgress.ratedConferences >= 3) {
                    triggerCelebration();
                }
                renderChallenges();
                $('#reviews-list').html(ratings[eventName].comments.slice().reverse().map(c => `<p>${c}</p>`).join(''));
                $('#review-comment').val('');
            }
        });

        $('#clear-rating').off('click').on('click', function() {
            const previousStarsLength = ratings[eventName].stars.length;
            ratings[eventName].stars = [];
            if (previousStarsLength > 0) {
                userProgress.rated -= previousStarsLength;
                if (event.category === "Conference") {
                    userProgress.ratedConferences -= previousStarsLength;
                    if (userProgress.ratedConferences < 0) userProgress.ratedConferences = 0;
                }
                if (userProgress.rated < 0) userProgress.rated = 0;
                saveProgress();
                renderChallenges();
            }
            $('#average-rating').text('0');
            $('#rating-count').text('0');
            $('.star').removeClass('filled');
        });

        $('#reviews-list').off('wheel').on('wheel', function(e) {
            e.preventDefault();
            const scrollAmount = e.originalEvent.deltaY * 2;
            this.scrollTop += scrollAmount;
        });

        $('.close').off('click').on('click', function() {
            modal.css('display', 'none');
        });
    };

    // Countdown Timer
    function startCountdown(eventDate, timerId) {
        const countDownDate = new Date(eventDate).getTime();
        const timer = document.getElementById(timerId);

        const interval = setInterval(() => {
            const now = new Date().getTime();
            const distance = countDownDate - now;

            if (distance < 0) {
                clearInterval(interval);
                timer.textContent = "Event Started!";
                return;
            }

            const days = Math.floor(distance / (1000 * 60 * 60 * 24));
            const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            const seconds = Math.floor((distance % (1000 * 60)) / 1000);

            timer.textContent = `${days}d ${hours}h ${minutes}m ${seconds}s`;
        }, 1000);
    }

    // Format Date with Time
    function formatDate(dateString) {
        const date = new Date(dateString);
        const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
        return date.toLocaleString('en-US', options);
    }

    // Update Category Filter
    function updateCategoryFilter() {
        const counts = {};
        events.forEach(e => counts[e.category] = (counts[e.category] || 0) + 1);
        $('#category').html(`
            <option value="All">All Events (${events.length})</option>
            <option value="Concert">Concert (${counts.Concert || 0})</option>
            <option value="Conference">Conference (${counts.Conference || 0})</option>
            <option value="Exhibition">Exhibition (${counts.Exhibition || 0})</option>
            <option value="Workshop">Workshop (${counts.Workshop || 0})</option>
            <option value="Festival">Festival (${counts.Festival || 0})</option>
        `);
    }

    // Render Events
    function renderEvents() {
        const eventList = $('#event-list');
        eventList.empty();

        events.forEach(event => {
            const avgRating = ratings[event.name] && ratings[event.name].stars.length > 0 
                ? (ratings[event.name].stars.reduce((a, b) => a + b, 0) / ratings[event.name].stars.length).toFixed(1) 
                : 'N/A';
            const voteCount = ratings[event.name] ? ratings[event.name].stars.length : 0;
            const eventDiv = $(`
                <div class="event slide-in" 
                     data-name="${event.name}" 
                     data-category="${event.category}" 
                     data-description="${event.description}" 
                     data-date="${event.date}" 
                     data-location="${event.location}">
                    <img src="${categoryImages[event.category]}" alt="${event.category} image" class="event-image">
                    <h3>${event.name}</h3>
                    <p>${formatDate(event.date)} | ${event.location}</p>
                    <p id="${event.timerId}"></p>
                    <p class="rating-display">Rating: ${avgRating} (${voteCount} votes)</p>
                    <button class="btn" onclick="showEventDetails('${event.name}')">Details</button>
                    ${event.isUserCreated ? `
                        <button class="btn delete-btn" onclick="deleteEvent('${event.name}')">Delete</button>
                        <button class="btn edit-btn" onclick="editEvent('${event.name}')">Edit</button>
                    ` : ''}
                </div>
            `);
            eventList.append(eventDiv);
            startCountdown(event.date, event.timerId);
        });

        const eventSelect = $('#event');
        eventSelect.empty();
        events.forEach(event => {
            eventSelect.append(`<option value="${event.name}">${event.name}</option>`);
        });

        updateCategoryFilter();
    }

    // Render Challenges
    function renderChallenges() {
        const challengeList = $('#challenge-list');
        challengeList.empty();

        challenges.forEach(challenge => {
            const progress = {
                "attend-3": userProgress.attended,
                "create-2": userProgress.created,
                "rate-5": userProgress.rated,
                "invite-10": userProgress.invited,
                "attend-concert-2": userProgress.attendedConcerts,
                "create-festival-1": userProgress.createdFestivals,
                "rate-conference-3": userProgress.ratedConferences,
                "attend-workshop-4": userProgress.attendedWorkshops
            }[challenge.id] || 0;
            const isCompleted = progress >= challenge.goal;
            const challengeDiv = $(`
                <div class="challenge ${isCompleted ? 'completed' : ''}">
                    <h3>${challenge.name}</h3>
                    <p>${challenge.description} (${progress}/${challenge.goal})</p>
                    <div class="badge">${challenge.badge}</div>
                </div>
            `);
            challengeList.append(challengeDiv);
        });
    }

    // Delete Event
    window.deleteEvent = function(eventName) {
        const event = events.find(e => e.name === eventName);
        if (event && event.isUserCreated) {
            userProgress.created--;
            if (event.category === "Festival") {
                userProgress.createdFestivals--;
                if (userProgress.createdFestivals < 0) userProgress.createdFestivals = 0;
            }
            if (userProgress.created < 0) userProgress.created = 0;
            saveProgress();
            renderChallenges();
        }
        events = events.filter(e => e.name !== eventName);
        tempEvents = tempEvents.filter(e => e.name !== eventName);
        delete ratings[eventName];
        saveTempEvents();
        renderEvents();
        filterEvents();
    };

    // Edit Event
    window.editEvent = function(eventName) {
        const event = events.find(e => e.name === eventName);
        $('#event-name').val(event.name);
        $('#event-category').val(event.category);
        $('#event-date').val(event.date.split('T')[0]);
        $('#event-time').val(event.date.split('T')[1].slice(0, 5));
        $('#event-location').val(event.location);
        $('#event-description').val(event.description);
        if (event.isUserCreated) {
            userProgress.created--;
            if (event.category === "Festival") {
                userProgress.createdFestivals--;
                if (userProgress.createdFestivals < 0) userProgress.createdFestivals = 0;
            }
            if (userProgress.created < 0) userProgress.created = 0;
            saveProgress();
            renderChallenges();
        }
        events = events.filter(e => e.name !== eventName);
        tempEvents = tempEvents.filter(e => e.name !== eventName);
        delete ratings[eventName];
        saveTempEvents();
        renderEvents();
        filterEvents();
        $('html, body').animate({ scrollTop: $('#create-event').offset().top }, 'smooth');
    };

    // Event Filtering
    function filterEvents() {
        const category = $('#category').val();
        const sortOrder = $('#sort').val();
        const searchTerm = $('#search').val().toLowerCase();
        const eventsElements = $('#event-list .event');

        const headingText = sortOrder === 'asc' ? 'Upcoming' : 'Past';
        $('#events-heading').html(`${headingText} <span>Events</span>`);

        eventsElements.each(function() {
            const eventCategory = $(this).data('category');
            const eventName = $(this).data('name').toLowerCase();
            const eventDate = new Date($(this).data('date'));
            const now = new Date();

            let show = true;
            if (category !== 'All' && eventCategory !== category) show = false;
            if (!eventName.includes(searchTerm)) show = false;
            if (sortOrder === 'asc' && eventDate < now) show = false;
            if (sortOrder === 'desc' && eventDate > now) show = false;

            $(this).css('display', show ? 'block' : 'none');
        });
    }

    $('#category').on('change', filterEvents);
    $('#sort').on('change', filterEvents);
    $('#search').on('input', filterEvents);

    // Create Event Form Submission
    $('#create-event-form').on('submit', function(e) {
        e.preventDefault();
        const name = $('#event-name').val();
        const category = $('#event-category').val();
        const date = $('#event-date').val();
        const time = $('#event-time').val();
        const eventDateTime = `${date}T${time}:00`;
        const location = $('#event-location').val();
        const description = $('#event-description').val();
        timerCounter++;
        const timerId = `timer-${timerCounter}`;

        const newEvent = { name, category, description, date: eventDateTime, location, timerId, isUserCreated: true };
        
        tempEvents.push(newEvent);
        saveTempEvents();
        events.push(newEvent);

        const previousCreated = userProgress.created;
        const previousCreatedFestivals = userProgress.createdFestivals;
        userProgress.created++;
        if (category === "Festival") userProgress.createdFestivals++;
        saveProgress();
        if (previousCreated < 2 && userProgress.created >= 2) {
            triggerCelebration();
        }
        if (previousCreatedFestivals < 1 && userProgress.createdFestivals >= 1) {
            triggerCelebration();
        }
        renderEvents();
        renderChallenges();
        filterEvents();

        $('#confirmation-text').text(`Event "${name}" created successfully!`);
        $('#confirmation-modal').fadeIn().delay(2000).fadeOut();
        this.reset();
    });

    // Registration Form Submission
    $('#registration-form').on('submit', function(e) {
        e.preventDefault();
        const name = $('#name').val();
        const email = $('#email').val();
        const phone = $('#phone').val();
        const eventName = $('#event').val();
        const event = events.find(e => e.name === eventName);
        const previousAttended = userProgress.attended;
        const previousAttendedConcerts = userProgress.attendedConcerts;
        const previousAttendedWorkshops = userProgress.attendedWorkshops;
        userProgress.attended++;
        if (event.category === "Concert") userProgress.attendedConcerts++;
        if (event.category === "Workshop") userProgress.attendedWorkshops++;
        saveProgress();
        if (previousAttended < 3 && userProgress.attended >= 3) {
            triggerCelebration();
        }
        if (previousAttendedConcerts < 2 && userProgress.attendedConcerts >= 2) {
            triggerCelebration();
        }
        if (previousAttendedWorkshops < 4 && userProgress.attendedWorkshops >= 4) {
            triggerCelebration();
        }
        renderChallenges();
        $('#confirmation-text').text(`Thank you, ${name}! Registered for ${eventName}. Confirmation sent to ${email}. Phone: ${phone}`);
        $('#confirmation-modal').fadeIn().delay(2000).fadeOut();
        this.reset();
    });

    // Contact Form Submission
    $('#contact-form').on('submit', function(e) {
        e.preventDefault();
        const name = $('#contact-name').val();
        const email = $('#contact-email').val();
        const phone = $('#contact-phone').val();
        const message = $('#contact-message').val();
        $('#confirmation-text').text(`Thank you, ${name}! Your message has been received. We'll contact you at ${email} or ${phone}.`);
        $('#confirmation-modal').fadeIn().delay(2000).fadeOut();
        this.reset();
    });

    // Initial Render
    renderEvents();
    renderChallenges();
});